		function tf_c_DWN($tf)
		{
			date_default_timezone_set("Africa/Lagos");
			if ($tf == 1) {
				return date('M d, Y') . " 06:00:00";
			} else if ($tf == 2) {
				return date('M d, Y') . " 12:00:00";
			} else if ($tf == 3) {
				return date('M d, Y') . " 18:00:00";
			} else {
				return date('M d, Y', strtotime("+ 1day")) . " 00:00:00";
			}
		}

		function getFDTF()
		{
			date_default_timezone_set("Africa/Lagos");
			$fd_d_tf =  date("Hi");

			if ($fd_d_tf > 0 && $fd_d_tf <= 600) {
				$tf = 1;
			} else if ($fd_d_tf > 600 && $fd_d_tf <= 1200) {
				$tf = 2;
			}else if ($fd_d_tf > 1200 && $fd_d_tf <= 1800) {
				$tf = 3;
			} else {
				$tf = 4;
			}

			return $tf;
		}

		function Capita($value)
		{
			$text_builder = array();
			$o_array = explode(" ", $value);
			foreach ($o_array as $key => $value) {
				$text_builder[] = strtoupper(substr($value, 0,1)) . substr($value, 1);
			}

			return trim(join(" ", $text_builder));
		}

