		function coveR($r1, $rt1)
		{
			$r_ = (($r1/($rt1*5))*100);
			if (is_nan($r_)) {
				return 100;
			} else {
				return 100-$r_;
			}
		}

		function discount($v1, $v2, $ty_)
		{
			$n_p = $v1-$v2;
			if ($ty_ == 1)	 {
				return round((($n_p/$v1)*100)-100, 2) . "%";
			} else {
				return round((($n_p/$v1)*100)-100, 5) . "%";
			}
			
		}

