		function discountR($_text_, $disc)
		{
			global $class_;
			global $cur_currency;

			$_text_1 = explode("-", $_text_)[0];
			$_text_2 = explode("-", $_text_)[1];

			if ($_text_2 > 0) {
				return $class_->_currency($cur_currency, $_text_1+(($disc/100)*$_text_1)) . " - " . trim($class_->_currency($cur_currency, $_text_2+(($disc/100)*$_text_2)));
			} else {
				return $class_->_currency($cur_currency, $_text_1+(($disc/100)*$_text_1));
			}
		}

		function PriceR($_text_)
		{
			global $class_;
			global $cur_currency;

			$_text_1 = explode("-", $_text_)[0];
			$_text_2 = explode("-", $_text_)[1];

			if ($_text_2 > 0) {
				return $class_->_currency($cur_currency, $_text_1) . "-" . trim(substr($class_->_currency($cur_currency, $_text_2), 3));
			} else {
				return $class_->_currency($cur_currency, $_text_1);
			}

		}

