		function sendMail($receiver_mail, $mail_subject, $contentMail, $cc)
		{
			$to = strtolower($receiver_mail);
			$subject = $mail_subject;

			// Get HTML contents from file
			$htmlContent = $contentMail;

			// Set content-type for sending HTML email
			$headers = "MIME-Version: 1.0" . "\r\n";
			$headers .= "Content-type:text/html;charset=UTF-8" . "\r\n";

			// Additional headers
			$headers .= 'From: yossell <no-reply@yossell.com>' . "\r\n";
			$headers .= 'Cc: '.$cc. "\r\n";

			mail($to, $subject, $htmlContent, $headers);
		}

		function _currency($currency, $org_val)
		{
			return $currency . "" . number_format($org_val) . ".00";
		}

